  transform: translateY(-$size + $outline-width) rotate(135deg);
} @else if $direction == "left" {
  transform: translateX($outline-width) rotate(225deg);
} @else {
  @error "Invalid arrow direction: expected `up`, `right`, `down` or `left`, got `#{$direction}`";
}

