  @each $show-breakpoint in $show-breakpoints {
    $width: get-breakpoint-width($show-breakpoint, $breakpoints);
    @include mq($show-breakpoint, $breakpoints: $breakpoints) {
      content: "#{$show-breakpoint}  #{$width} (#{px2em($width)})";
    }
  }
}

